const titleStyle = {
    margin: "auto",
    padding: "8px 24px",
    width: "max-content",
    textAlign: "center",
    fontFamily: "trebuchet ms"
  };
  
  const textBoxContainerStyle = {
    background: "lightgray",
    position: "relative",
    margin: "18px auto",
    width: 380,
    height: 232,
    borderRadius: 8,
    boxShadow: "0 0 10px 2px rgba(0,0,0,0.4)"
  };
  
  const textAreaStyle = {
    position: "absolute",
    width: "90%",
    top: "5%",
    bottom: "5%",
    left: "5%",
    right: "5%",
  };
  
  const settingsStyle = {
    display: "flex",
    flexDirection: "column",
    margin: "auto",
    width: 300,
    textAlign: "center",
    fontFamily: "trebuchet ms",
  };
  
  const largeButton = {
    display: "block",
    padding: "12px",
    margin: "18px auto",
    width: "300px",
  };
  
  const smallButton = {
    width: "35%",
  };
  
  const counterStyle = {
    display: "flex",
    margin: "auto",
    width: 300,
  };
  
  
  
  // COMPONENTS
  const Title = () => {
    return (
      <div style={titleStyle}>
        <h1>React Caesar Cipher!</h1>
        <h2>Or, at least, a current attempt...</h2>
      </div>
    );
  };
  
  class TextBoxContainer extends React.Component {
    constructor(props) {
      super(props);
    }
    render() {
      return (
        <div style={textBoxContainerStyle}>
          <textarea onChange={this.props.handleInput} value={this.props.value} style={textAreaStyle} />
        </div>
      );
    }
  };
  
  class Counter extends React.Component {
    constructor(props) {
      super(props);
    }
    render() {
      return (
        <div style={settingsStyle}>
          <button onClick={this.props.resetOffset} style={largeButton}>Reset</button>
          <div style={counterStyle}>
            <button onClick={this.props.decrement} style={smallButton}>Decrement</button>
            <h2 style={{width: "40%"}}>{this.props.offset}</h2>
            <button onClick={this.props.increment} style={smallButton}>Increment</button>
          </div>
          <button style={largeButton} onClick={this.props.offsetString}>Encrypt</button>
        </div>
      );
    }
  };
  
  
  
  // MAIN APP COMPONENT
  class Test extends React.Component {
    constructor(props) {
      super(props);
      this.state = ({
        input: "",
        offset: 1,
        output: ""
      });
      this.handleInput = this.handleInput.bind(this);
      this.offsetString = this.offsetString.bind(this);
      this.increment = this.increment.bind(this);
      this.decrement = this.decrement.bind(this);
      this.resetOffset = this.resetOffset.bind(this);
    }
  
    handleInput(event) {
      this.setState({
        input: event.target.value
      });
    }
  
    offsetString() {
      const newStr = [...this.state.input].map(L => String.fromCharCode(L.charCodeAt(0)+this.state.offset)).join("");
      this.setState({
        output: newStr
      });
    }
  
    increment() {
      this.setState({
        offset: this.state.offset + 1
      });
    }
  
    decrement() {
      this.setState({
        offset: this.state.offset - 1
      });
    }
  
    resetOffset() {
      this.setState({
        offset: 0
      });
    }
  
    render() {
      return (
        <div style={{background: "#ddd", display: "flex", flexDirection: "column", margin: 0, height: "98vh"}}>
          <Title />
          <TextBoxContainer handleInput={this.handleInput} />
          <Counter offset={this.state.offset} offsetString={this.offsetString} increment={this.increment} decrement={this.decrement} resetOffset={this.resetOffset} />
          <TextBoxContainer value={this.state.output} />
        </div>
      );
    }
  };
  
  
  
  // RENDERING
  export default Test;
